% SR = 44100.0 %Sample rateSW  = 16  %  /* octaves per second */DUR= 20  % sweep durationFAMPEND = 30  % hum?RD  = 10 % /* ms rampduration */AMP  = 103  % ampfacFL = [500, 1000, 2000, 4000];IS=[1000, 900, 800, 700, 600, 500, 400, 300, 250, 200,...195, 190, 185, 180, 175, 170 ,165, 160, 155, 150,... 145, 140, 135, 130, 125, 120, 115, 110, 105, 100,... 95, 90, 85, 80, 75, 70, 65, 60, 55, 50,... 45, 40, 35, 30, 25, 20, 15, 10, 5, 0] ; Sample_int  = 1000 / SRfl = FL(4) %target freqfh=fl*(2^(-SW*DUR/1000.0))  % startfreq % berechne sweep dazwischen t =0:Sample_int/1000:DUR/1000; diffsqrt = abs(sqrt(fl) - sqrt(fh)); diffsqrt_step = diffsqrt / length(t); sqrtvec = sqrt(fh)+diffsqrt_step : diffsqrt_step : sqrt(fl); powervec = sqrtvec .^2; for index = 1 : length(t)sweep(index) = sin(2*pi*powervec(index)*t(index));end% ramping of sweep  M2 =10 * 44100 / 1000;squarecos1 = (cos(pi/2:(pi-pi/2)/M2:pi-(pi-pi/2)/M2)).^2;squarecos2 = (cos(0:(pi-pi/2)/M2:pi/2-(pi-pi/2)/M2)).^2;sweep_tap = [sweep(1:M2) .* squarecos1 sweep(M2+1:length(t) - M2) sweep(length(t)-M2+1 : length(t)).*squarecos2];pausesound((sweep_tap), 44100)plot((sweep_tap))pausewavwrite((sweep_tap), 44100, 'elnino:temp:up_4000_20ms.wav')