% tmp5% mittelt matrizen oder deren maxima disp('neu')subjectvec = ['01'; '02'; '04'; '07'; '08'; '09';'10'; '11'; '12';'13';'14';'15';'17'; '18';'19'; '21'; '22'; '24';'25'];condvec = ['neg'; 'ntr'; 'pos'];n = input('insert number of rows   ')m = input('insert number of cols    ')	for cond = 1 : length (condvec)		maxsumvec = zeros(1,n);		summat = zeros(n,m);		for vp = 1 : length(subjectvec(:,1));			file	= ([ subjectvec(vp,:) condvec(cond,:) 'win'])			path = ['ubies:emosteady:winmats:']			eval(['load ' path file])			dummymat = eval(['X' file]);			for elek = 1:129				dummymat(elek,:) = dummymat(elek,:) - dummymat(elek,1); 			end						for elek = 1:129				maxvec(elek) = max(dummymat(elek,:));			end			maxsumvec = maxsumvec + maxvec;			summat = summat + dummymat;					end		meanmat = summat ./ length(subjectvec(:,1));		meanmatname = (['GM' condvec(cond,:) 'win'])		eval(['save ubies:emosteady:winmats:' meanmatname ' meanmat -ascii'])				meanmaxvec = maxsumvec ./ length(subjectvec(:,1));		meanmaxname = (['GM' condvec(cond,:) 'max'])		eval(['save ubies:emosteady:winmats:' meanmaxname ' meanmaxvec -ascii'])			end					