% vp schleife fuer getacquiscurve% berechnet mittlere acquisitioncurvevpvec = ['03'; '05'; '06'; '08'; '09';'11'; '12'; '13'; '15'; '17'; '18';'19';'20'; '21']for index = 1 : length(vpvec(:,1))	actFilePath = (['ubies:As_exps:exoperant:behav:VP' vpvec(index,:) '.TXT'])	[RTvecOKmavg, RTvecKmavg, RewardvecOK, RewardvecK] = getacquiscurve(actFilePath); 	if index ==1		meanRTvecOK = RTvecOKmavg;		meanRTvecK = RTvecKmavg;		meanRWdvecOK = RewardvecOK;		meanRWdvecK = RewardvecK;	else	meanRTvecOK = meanRTvecOK + RTvecOKmavg;	meanRTvecK = meanRTvecK + RTvecKmavg;	meanRWdvecOK = meanRWdvecOK+RewardvecOK;	meanRWdvecK = meanRWdvecK+RewardvecK;	endendmeanRTvecOK = meanRTvecOK ./ length(vpvec(:,1));meanRTvecK = meanRTvecK ./ length(vpvec(:,1));meanRWdvecOK = meanRWdvecOK ./length(vpvec(:,1));meanRWdvecK = meanRWdvecK ./length(vpvec(:,1));