% berechnet Meanpotentials aus ATG.ar konstanz file fuer ein anzugebendes Zeit-% fenster im Batchbetriebfunction [] = write_rms_chris(filemat, bslvec);%clearfor fileindex = 1 : size(filemat, 1)% baseline abziehen[ERPmat] = ReadAvgFile(filemat(fileindex,:));   % liest ein	if ~isempty(bslvec);				for chan = 1 : size(ERPmat,1)		BslCorMat(chan,:) = ERPmat(chan,:)-mean(ERPmat(chan,bslvec));		end	else	BslCorMat = ERPmat;	end	rmsvec =  rms_mat(BslCorMat);eval(['save ' filemat(fileindex,:) '.rms  rmsvec  -ascii']) end